public class Alphabets {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		System.out.println("Enter the value of n: ");
		int n=sc.nextInt();
		//Alphabet G
		for(int i=0;i<=n-1;i++) {
			for(int j=0;j<=n-1;j++) {
				if((j==0 && i!=0 && i!=(n-1))
					|| (i==0 && j!=0 && j<(n/2))
					|| (i==(n/2) && j>(n/4) && j<(n/2))
					|| (j==(n/4) && i>(n/2))
					|| (j==(n/2) && i>(n/2))
					|| (i==(n-1) && j>0 && j<=(n/4)))
					{
					System.out.print("# ");
				}
				else
				{
					System.out.print("  ");
				}
			}
			
			//Logic for R
			for(int j=0;j<=n-1;j++) {
				if(j==0 || (j==(n/2) && i<(n/2)) || (i==(n/2) && j<(n/2)) || (i==0 && j<(n/2)) || i-j==(n/2)) {
					System.out.print("# ");
				}
				else
				{
					System.out.print("  ");
				}
			}
			
			System.out.print("  ");
			//Alphabet N
				for(int j=0;j<=n-1;j++) {
					if(j==0  
						|| i==j
						|| j==(n-1))
						{
						System.out.print("# ");
					}
					else
					{
						System.out.print("  ");
					}
				}
				System.out.print("  ");
				//Alphabet A
				for(int j=0;j<=n-1;j++) {
					if(j==0 || j==n/2 
						|| (i==(n/2) && j<=(n/2))
						|| (i==0 && j<=n/2))
						{
						System.out.print("# ");
					}
					else
					{
						System.out.print("  ");
					}
				}
				System.out.print("  ");
				//Logic for R
				for(int j=0;j<=n-1;j++) {
					if(j==0 || (j==(n/2) && i<(n/2)) || (i==(n/2) && j<(n/2)) || (i==0 && j<(n/2)) || i-j==(n/2)) {
						System.out.print("# ");
					}
					else
					{
						System.out.print("  ");
					}
				}
				
				//Alphabet E
				for(int j=0;j<=n-1;j++) {
					if(j==0
						|| (i==0) 
						|| (i==(n/2) && j!=(n-1))
						|| (i==(n-1))) {
						System.out.print("# ");
					}
					else
					{
						System.out.print("  ");
					}
				}
			
				System.out.print("  ");
				
				//Alphabet N
				for(int j=0;j<=n-1;j++) {
					if(j==0  
						|| i==j
						|| j==(n-1))
						{
						System.out.print("# ");
					}
					else
					{
						System.out.print("  ");
					}
				}
				System.out.print("  ");
				
				
				//Alphabet D
				for(int j=0;j<=n-1;j++) {
					if(j==0 ||
							(i==0 && j!=(n-1))
							|| (i==(n-1) && j!=(n-1))
							|| (j==(n-1) && i!=0 && i!=(n-1)))
							
						{
						System.out.print("# ");
					}
					else
					{
						System.out.print("  ");
					}
				}
				System.out.print("  ");
				//Logic for R
				for(int j=0;j<=n-1;j++) {
					if(j==0 || (j==(n-1) && i<(n/2)) || (i==(n/2) && j<(n-1)) || (i==0 && j<(n-1)) || i-j==(n/2)) {
						System.out.print("# ");
					}
					else
					{
						System.out.print("  ");
					}
				}
				
				//Alphabet A
				for(int j=0;j<=n-1;j++) {
					if(j==0 || j==n/2 
						|| (i==(n/2) && j<=(n/2))
						|| (i==0 && j<=n/2))
						{
						System.out.print("# ");
					}
					else
					{
						System.out.print("  ");
					}
				}
				System.out.print("  ");
		
			System.out.println();
		}
	}
}